#include<stdio.h>
using namespace std;
int mp[550][550];
int r,c;
struct
{
	int x,y;
}s[30000];
int check(int k)
{
	if(s[k].x<r-1&&mp[s[k].x+1][s[k].y]==2)
	return 1;
	if(s[k].x>0&&mp[s[k].x-1][s[k].y]==2)
	return 1;
	if(s[k].y<c-1&&mp[s[k].x][s[k].y+1]==2)
	return 1;
	if(s[k].y>0&&mp[s[k].x][s[k].y-1]==2)
	return 1;
	return 0;
}
int main()
{

	scanf("%d%d",&r,&c);
	int i,j,k;
	char a;
	int lg;
	lg=1;
	k=0;
	for(i=0;i<r;i++)
	{
		getchar();
		for(j=0;j<c;j++)
		{
			scanf("%c",&a);
			if(a=='S')
			{
				s[k].x=i;
				s[k].y=j;
				k++;
				mp[i][j]=1;
			}
			else if(a=='W')
			mp[i][j]=2;
		}
	}
	for(i=0;i<k;i++)
	{
		if(check(k))
		lg=0;
	}
	if(lg)
	{
		printf("Yes\n");
		for(i=0;i<r;i++)
		{
			for(j=0;j<c;j++)
			{
				if(mp[i][j]==0)
				printf("D");
				else if(mp[i][j]==1)
				printf("S");
				else if(mp[i][j]==2)
				printf("W");
			}
			printf("\n");
		}
	}
	return 0;
}

