#include <stdio.h>
long long min(long long a, long long b)
{
    return a < b ? a : b;
}

int main()
{
    int t;
    long long a, b, k;
    scanf("%d", &t);
    while (t--)
    {
        scanf("%lld %lld", &a, &b);
        if (a < b)
        {
            t = a; a = b; b = t;
        }
        if (a == b)
        {
            printf("0 0\n");
        }
        else if (a - b == 1)
        {
            printf("1 0\n");
        }
        else {
            k = a - b;
            printf("%lld %lld\n", k, min(b % k, k - b % k));
        }
    }
    return 0;
}

