#include <stdio.h>
#include <string.h>
#include <math.h>
#include <stdlib.h>
#define ll long long
#define mod (ll)(1e9 + 7)

ll bpower(ll num, ll power)         // Function that raises num to power 'power' in log(power) time
{
    ll ans = 1, multiply = num;
    while(power != 0)
    {
        if (power % 2 == 1)
            ans = ((ans % mod) * (multiply % mod)) % mod;
        multiply = ((multiply % mod) * (multiply % mod)) % mod;
        power >>= 1;
    }

    return ans;
}

int main(void)
{
    ll n;
    scanf("%lli", &n);

    ll a = bpower(4, n);
    ll b = bpower(2, n);

    ll up = (a + b) / 2;
    printf("%lli\n", up);
}

