#include <stdio.h>
int ans[1000000];
int gcd(int a,int b){while(b){int r=a%b;a=b;b=r;}return a;}
int main()
{
    int i,j,x,y,N,A,B;
    scanf("%d%d%d",&N,&A,&B);
    if(N%gcd(A,B)) printf("-1\n");
    else
    {
        for(x=0;x<=N;x+=A)
        {
            y=N-x;
            if(y%B==0) break;
        }
        y/=B;
        x/=A;
        int pos=0;
        for(i=0;i<x;i++)
        {
            for(j=0;j<A-1;j++)
                ans[pos+j]=pos+j+1;
            ans[pos+j]=pos;
            pos+=A;
        }
        for(i=0;i<y;i++)
        {
            for(j=0;j<B-1;j++)
                ans[pos+j]=pos+j+1;
            ans[pos+j]=pos;
            pos+=B;
        }
        for(i=0;i<N;i++)
            printf("%d ", ans[i]+1);
        printf("\n");
    }
    return 0;
}

