#include <string.h>

int dsf(int, int);

int main(void)
{
    int t;
    scanf("%d", &t);
    while (t--)
    {
        int l, r, x;
        int a, b;
        scanf("%d%d%d%d%d", &l, &r, &x, &a, &b);

        if (a > b)
        {
            int t = a;
            a = b;
            b = t;
        }

        if (a == b)
        {
            printf("0\n");
            continue;
        }

        else if (dsf(a, b) >= x)
        {
            printf("1\n");
            continue;
        }

        else if (dsf(b, l) < x && dsf(b, r) < x)
        {
            printf("-1\n");
            continue;
        }

        else if (b + x <= r || a - x >= l)
        {
            printf("2\n");
            continue;
        }

        else if (a + x <= r || b - x >= l)
        {
            printf("3\n");
            continue;
        }
    }
    return 0;
}

int dsf(int x, int y)
{
    return x - y < 0 ? y - x : x - y;
}

