#include <stdio.h>
#define rep(i,n) for(int i=0; i<(int)(n); i++)
#define min(l,r) ((l)<(r)?l:r)

int n, m;
int a[202020];
int ans = 0;
int cnt[202020] = {0};

void solve() {
    rep(i, n) rep(j, m) a[m*i+j]--;
    rep(j, m) {
        rep(i, n) cnt[i] = 0;
        rep(i, n) {
            if(a[m*i+j] % m == j){
                cnt[(i - a[m*i+j]/m + n)%n]++;
            }
        }
        int tmp = n;
        rep(i, n) {
            tmp = min(tmp, n - cnt[i] + i);
        }
        ans += tmp;
    }
}

int main(){
    scanf("%d%d", &n, &m);
    rep(i, n) rep(j, m) scanf("%d", &a[m*i+j]);
    solve();
    printf("%d", ans);
}

