#define N 100000
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int a[N + 1];
int b[N + 1];

void diff(const int *a, const int *b) {
	while(*a) {
		if (*a != *b) {
			fprintf(stderr, "%d\n", *a);
			a++;
		} else {
			a++;
			b++;
		}
	}
}

int cmp(const void *a, const void *b) {
	if (*(int *)a < *(int *)b)
		return -1;
	if (*(int *)a == *(int *)b)
		return 0;
	return 1;
}

int main(void) {
	int n;
	scanf("%d", &n);
	for (int i = 0; i < n; i++)
		scanf("%d", &a[i]);
	qsort(a, n, sizeof(int), cmp);

	for (int i = 0; i < n - 1; i++)
		scanf("%d", &b[i]);
	qsort(b, n - 1, sizeof(int), cmp);
	diff(a, b);
	memcpy(a, b, n * sizeof(int));

	int i;
	for (i = 0; i < n - 2; i++)
		scanf("%d", &b[i]);
	b[i] = 0;
	qsort(b, n - 2, sizeof(int), cmp);
	diff(a, b);
	return 0;
}

