#include<stdlib.h>
#include<ctype.h>
int n;//n为奇数
char G[305][305];
int flag;
void slove()
{   flag=0;int sym=0;
    int i,j;
    for (i=0;i<n;i++)
    {
        getchar();
        for (j=0;j<n;j++)
            scanf("%c",&G[i][j]);
    }//读图完毕，开始判断
    //先判断对角线元素是否相同
    for (i=0;i<n-1;i++)
    if (G[i][i]!=G[i+1][i+1] || G[i][n-1-i]!=G[i+1][n-2-i]){flag=1;break;}
    char search=G[0][1];
    for (i=0;i<n;i++)
    {
        for (j=0;j<n;j++)
        {
            if (i==j || i+j==n-1) continue;
            if (G[i][j]!=search) {flag=1;sym=1;break;}
        }
        if (sym) break;
    }
    if (flag) printf("NO\n");
    else printf("YES\n");
}
int main()
{
    //freopen("sample.txt","r",stdin);
    while (scanf("%d",&n)==1)
        slove();
    return 0;
}

