        int lower = i * (i + 2);
        int upper = min(x - x % i, y * i + i);
        int times = (upper - lower) / i + 1;
        ans += times;
    }
    printf("%d\n", ans);
    return;
}

int main() {
    int t;
    scanf("%d", &t);
    for (int i = 0; i < t; ++i) testcase();
    return 0;
}

