			arr[i] = 2*i-1;
			arr[i + n] = 2*i;
		}
		else {
			arr[i + n] = 2 * i - 1;
			arr[i] = 2 * i;
		}
		cnt++;
	}
	if (n % 2 == 0) {
		printf("NO");
	}
	else {
		printf("YES\n");
		for (int i = 1; i <= 2 * n; i++) {
			printf("%d ", arr[i]);
		}
	}

	return 0;
}

