    scanf("%d",&n);
    for(i=0;i<n;i++)
        scanf("%d",&c[i]);
    qsort(c,n,sizeof(int),cmp);
    for(i=n-1;i>=0;i--)
    {
        if(c[i]>2*c[0])
        {
            F=1000000;
            low=0;
            high=i;
            while(low<=high)
            {
            mid=(low+high)/2;
            if(c[i]<=2*c[mid])
            {
            high=mid-1;
            fr=mid;
            if(fr<F)
                F=fr;
            }
            else if(c[i]>2*c[mid])
            {
            low=mid+1;
            }
            }
            er=(n-1-i)+F;
            if(er<MIN)
            MIN=er;
        }
        else
            break;
    }
    if(er>0)
    printf("%d",MIN);
    else
    printf("0");
    return(0);
}

