int max(int a, int b){return a>b?a:b;}
int min(int a, int b){return a<b?a:b;}
int main()
{

	int n,k;
	scanf("%d %d",&n,&k);
	char str[n+2];
	scanf("%s",str);

	char s[n+2];
	int i;
	for(i=0;i<n;i++)
	{
		if(k==0)
		{
			s[i] = str[i];
			continue;
		}
		int l,r;
		l = str[i]-'a';
		r = 'z' - str[i];
		if(r <= k && l <= k)
		{
			int t = max(l,r);
			if(str[i] - t >'a')
				s[i] = str[i]-t;
			else
				s[i] = str[i]+t;
			k-=t;
			//printf("ONE\n");
		}
		else if((r> k && l > k))
		{
			if(str[i]-k > 'a')
				s[i] = str[i]-k;
			else
				s[i] = str[i]+k;
			k=0;
			//printf("TWO\n");
		}
		else
		{
			int t = min(l,r);
			if(t < k)
				t =k;
			if(str[i] - t >'a')
				s[i] = str[i]-t;
			else
				s[i] = str[i]+t;
			k-=t;
			//printf("Three\n");
		}
	}
	s[i] ='\0';
	if(k) printf("-1\n");
	else
	printf("%s\n",s );

	return 0;
}

