    int array[n];
    char s1[n + 1], s2[n + 1], s3[n + 1];

    for (int i = 0; i < n; i++)
    {
        scanf("%d", &array[i]);
        if (array[i] == 1)
        {
            s1[c1] = i + 1;
            c1++;
        }
        else if (array[i] == 2)
        {
            s2[c2] = i + 1;
            c2++;
        }
        else
        {
            s3[c3] = i + 1;
            c3++;
        }
    }
    teams = min(&c1, &c2, &c3);
    printf("%d\n", teams);
