        for (int i = 0; i < teams; i++)
            printf("%d %d %d\n", s1[i], s2[i], s3[i]);
    return 0; 
}

int min(int *a, int *b, int *c)
{
    int m = *a;
    if (*b < m)
        m = *b;
    if (*c < m)
        m = *c;
    return m;    
}

