long long max=0,min=0;
int n,i;
void Quick_Sort(int length[],int x,int y)
{
    int a=x,b=y,num=length[x];
    int temp;
    if(x<y)
    {
        while(a<b)
        {
            while(num<length[b] && a<b) b--;
            if(a<b)
            {
                temp=length[a];
                length[a]=length[b];
                length[b]=temp;
                a++;
            }
            while(length[a]<num && a<b) a++;
            if(a<b)
            {
                temp=length[a];
                length[a]=length[b];
                length[b]=temp;
                b--;
            }
        }
        Quick_Sort(length,x,a-1);
        Quick_Sort(length,a+1,y);
    }
}
int main()
{
    scanf("%d",&n);
    for(i=0;i<n;i++)
    {
