#include"stdio.h"
char cmmd[100100];
int midf(long long m)
{
	int l=0,r=64;
	int mid=32;
	int ans=0;
	while (l<=r){
	if (m>>mid) {l=mid+1;}
	else {ans=mid;r=mid-1;}
	mid=(l+r)>>1;
	}
	return ans;
}
long long judge(long long n,int sd)
{
	int xb=1;
	long long st=(n+1)>>1;
	int now=sd;
	char *s=cmmd;
	while (*s){
		switch (*s){
		case 'L' : {
			if ((xb<<1)<=n){
				xb<<=1;
				now--;
				st=st-(1<<(now));
			}
	                    	}break;
		case 'R':
			if (((xb<<1)+1)<=n){
				xb=(xb<<1)+1;
				now--;
				st=st+(1<<(now));
			}
	                    	break;
		case 'U' :{
			if (xb>>1){
				if (xb&1){
					st-=(1<<(now));now++;xb>>=1;
				}
				else {
					st+=(1<<(now));now++;xb>>=1;
				}
			}
		   }break;
		}
		s++;
	}
	return st;
}
main()
{
	int t;
	long long n;
	scanf("%I64d%d",&n,&t);
	int m;
	int sd=midf(n)-1;
	while (t--)
	{
		scanf("%d%s",&m,cmmd);
		printf("%I64d\n",judge(n,sd));

	}
}

