int main()
{
	int t = 0;
	scanf("%d", &t);
	while (t > 0)
	{
		int n = 0;
		scanf("%d", &n);
		int arr1[50];
		char arr2[50];
		
		for (int i = 0; i < n; i++)
		{
			scanf("%d", &arr1[i]);
		}

		scanf("%s", &arr2);

		//j记录head记录
		for (int j = 0; j < n; j++)
		{
			int head = j;
			int end = 0;
			int i = 0;
			// 当元素个数只有1的时候
				if (end + 1 == n)
				{
					printf("YES\n");
					break;
				}
			while (i < n)
			{
				
				//如果数字数组中有相同元素个数时进入字符数组对比是否相同
				if (arr1[head] == arr1[end] && head != end)
				{
					if (arr2[head] == arr2[end])
					{
						end++;
						i++;
						continue;
					}
					else if (arr2[head] != arr2[end])
					{
						printf("NO\n");
						goto end;
					}
				}
				
				//当int数组中数字不相同时，让end走，head记录要对比的元素
				if (arr1[head] != arr1[end]||head==end)
				{
					
					end += 1;
					i++;
					
				}


			}
			

			//当j==n-1时，代表没有出错
			if (j == n - 1)
			{
				printf("YES\n");
			}

			
			
				
		}
		

	end:
		t--;
	}




	return 0;
}

