#include <stdio.h>

int do_calc(long long n)
{
    int ans;
    ans=0;
    while (n)
    {
        ans+=n%2;
        n/=2;
    }
    return ans;
}

int do_search(long long n,int m,long long a[m])
{
    int i,ans;
    for (i=m-1;i>0;i--)
    {
        if (a[i]==n) return 1;
        if (a[i]<n)
        {
            m=i+1;
            break;
        }
    }
    ans=do_calc(n);
    for (i=m-1;i>0;i--)
    {
        if (1+do_search(n-a[i],i-1,a)<ans) ans=1+do_search(n-a[i],i-1,a);
    }
    return ans;
}

int main()
{
    int i,j,t,ans;
    long long n,a[15];
    a[0]=1;
    for (i=1;i<15;i++) a[i]=i*a[i-1];
    scanf("%d",&t);
    for (i=0;i<t;i++)
    {
        scanf("%lld",&n);
        ans=do_search(n,15,a);
        printf("%d\n",ans);
    }
}

