#include <stdio.h>
int main()
{
	int t;
	int row, j, col, i;
	int tem[30][30];
	scanf("%d", &t);
	getchar();
	while (t--)
	{
		memset(tem, 0, sizeof(tem));
		scanf("%d%d", &row, &col);
		getchar();

		if (col % 2 == 0)
		{
			tem[0][0] = 0;
			i = 1;
		}
		else
		{
			tem[0][0] = 1;
			i = 2;
		}//j or o,csh
		for (;; i += 2)
		{
			if (i > col)
				break;
			else
				tem[0][i] = 1;
		}//The first line.
		for (j = 2;; j += 2)
		{
			if (j > row)
				break;
			else
				tem[j][col - 1] = 1;
		}//Right
		for (i = col - 3;; i -= 2)
		{
			if (i < 0)
				break;
			else
				tem[row - 1][i] = 1;
		}//Bottom.
		for (j = row - 3;; j -= 2)
		{
			if (j < 0)
				break;
			else
				tem[j][0] = 1;
		}//Left.
		if (tem[0][0] == 1 || tem[0][1])
			tem[1][0] = 0;//The last.
		for (j = 0; j < row; j++)
		{
			for (i = 0; i < col; i++)
				printf("%d", tem[j][i]);
			printf("\n");
		}
	}
	return 0;
}

