long long abs(long long a){
    return (a > 0 ? a : -a);
}
long long solve(int n, int * piles){
    long long sum = 0, prefix = 0;
    int difference[200005];
    for(int i = 0; i < n - 1; i++){
        difference[i] = piles[i+1] - piles[i];
        sum += abs(difference[i]);
        if(difference[i] < 0)
            prefix += difference[i];
    }
    return sum + abs(piles[0] + prefix);
}

