int palindrome(char x[1000],int a,int b){
    int i,j,f=1;
    for(i=a,j=b;i<=(b-a)/2;i++,j--){
        if(x[i]!=x[j]){
            f=0;
            break;
        }
    }
    if(f==1) return 1;
    else return 0;
}
