    int k,n,l,i=0,j;
    scanf("%s%d",s,&k);
    n=strlen(s);
    if(n%k!=0) printf("NO");
    else{
        l=n/k;
        while(k--){
            j=palindrome(s,i,i+l-1);
            i+=l;
            if(j==0) break;
        }
        if(j==0) printf("NO");
        else if(j==1) printf("YES");
    }
    return 0;
}

