#include<stdio.h>

int main()
{
//	freopen("input.txt", "r", stdin);

	long n;
	scanf("%ld", &n);

	while (n-- > 0)
	{
		long long p, q, b;
		scanf("%lld %lld %lld", &p, &q, &b);

		int flag = 0;
		//准备开始约分p，q
		if (p%q == 0) {
			printf("Finite\n");
//			printf("%lld %% %lld=0\n", p,q);
			continue;
		}
		else if (p > q)
			p = p%q;
		for (int i = 2; i<p; i++) {
			while (p % i == 0 && q % i == 0) {
				p /= i;
				q /= i;
			}
		}
		//准备处理下n
		int B[1000] = { 0 };
		int pos = 0;
		for (int i = 2; i <= b; i++) {
			if (b % i == 0) {
				B[pos++] = i;
				while (b % i == 0)
				{
					b /= i;
				}
			}
		}
		for (int pos = 0; B[pos] != 0; pos++) {
			if (q%B[pos] != 0) {
				flag = 1;
//				printf("It's %lld/B[%d]!=0 and B[%d]=%d\n", q, pos,pos,B[pos]);
			}
			else
				q /= B[pos];
		}
		if (flag == 0)
			printf("Finite\n");
		else
			printf("Infinite\n");
	}

}

