		scanf("%lld %lld %lld", &p, &q, &b);//excute p/q is or not a finite

		//对于p=0 和p是q的倍数的处理
		if (p == 0 || p % q == 0) {
			printf("Finite\n");
			continue;
		}

		ll k = gcd(p, q);
		q /= k;
		while ((k = gcd(q,b)) != 1) {
			while (k < MAX)
				k *= k;
			k = gcd(k, q);
			q /= k;
		}

		if (q == 1)
			printf("Finite\n");
		else
			printf("Infinite\n");
	}
}

