#include <stdio.h>

int i, x, y, n, m, succs[101][100], nsuccs[101], seen[101], cedge[101], ncycles ;

int dfs(int root, int from)
{
	int k ;

	seen[root] = 1 ;

	for (k = 0 ; k < nsuccs[root] ; k++)
	{
		if (!seen[succs[root][k]])
		{
			if (dfs(succs[root][k], root))
			{
				return 1 ;
			}				
		}
		else if (succs[root][k] != from && cedge[root] != succs[root][k])
		{
			cedge[succs[root][k]] = root ;

			if (++ncycles == 2)
			{
				return 1 ;
			}
		}
	}

	return 0 ;
}

int main(void)
{
	scanf("%d %d", &n, &m) ;

	for (i = 0 ; i < m ; i++)
	{
		scanf("%d %d", &x, &y) ;

		succs[x][nsuccs[x]++] = y ;
		succs[y][nsuccs[y]++] = x ;
	}

	if (n != m)
	{
		printf("NO") ;
	}
	else
	{
		dfs(1, 0) ;

		printf((ncycles == 1 ? "FHTAGN!" : "NO")) ;
	}

	return 0 ;
}

