#define PI 3.1415926535897
int n;
long long x[100010],y[100010],d[100010];
double mx,mn = 99999999999999999LL;
long long abs(long long a)
{
    return a>0?a:-a;
}
void work(int a,int b)
{
    double t = x[a]*x[b]+y[a]*y[b];
    if(t<d[a]||t<d[b]) return;
    t = abs((x[a]*y[b]-x[b]*y[a]))/sqrt((x[a]-x[b])*(x[a]-x[b])+(y[a]-y[b])*(y[a]-y[b]));
    if(t<mn) mn = t;
}
int main()
{
    int i;
    scanf("%d%I64d%I64d",&n,&x[0],&y[0]);
    for(i=1;i<=n;i++)
    {
        scanf("%I64d%I64d",&x[i],&y[i]);
        x[i] -= x[0]; y[i] -= y[0];
        d[i] = x[i]*x[i]+y[i]*y[i];
        if(d[i]>mx) mx = d[i];
        if(d[i]<mn) mn = d[i];
        work(i-1,i);
    }
    work(n,1);
    printf("%.9f",PI*(mx-mn));
    return 0;
}

