int is_ordinary(int val)
{
    int same_digit = val % 10;
    
    while (val) {
        if (same_digit != val % 10)
            return 0;
            
        val /= 10; 
    }
    
    return 1;
}

void ordinary_numbers(void)
{
    int number;
    scanf("%d", &number);
    
    int ordinary_count = 0;
    
    for (int i = 1; i < number; ++i)
        if (is_ordinary(i)) 
            ++ordinary_count;
            
    printf("%d\n", ordinary_count);
}
        

int main(void)
{
    int test_cases;
    scanf("%d", &test_cases);
    
    for (int i = 0; i < test_cases; ++i)
        ordinary_numbers();

    return 0;
}

