#include <stdio.h>

int get_exponentation(int base, int power)
{
	if (power == 0)
		return 1;

	int multiplication_factor = base;

	for (int i = 1; i < power; ++i)
		base *= multiplication_factor;

	return base;
}

int n_digit(int val)
{
	if (val == 0)
		return 1;

	int digit_counter = 0;

	while (val) {
		++digit_counter;

		val /= 10;
	}

	return digit_counter;
}


void ordinary_numbers(void)
{
	int number;
	scanf("%d", &number);

	int digit_number = n_digit(number);
	int first_digit = number / get_exponentation(10, (digit_number - 1));

	int boundary_value = 0;

	for (int i = 0, j = first_digit; i < digit_number; ++i)
		boundary_value += (get_exponentation(10, i) * first_digit);

	int total_ordinary_numbers = 0;

	if (number < boundary_value)
		total_ordinary_numbers = (digit_number - 1) * 9 + (first_digit - 1);
	else
		total_ordinary_numbers = (digit_number - 1) * 9 + first_digit;

	printf("%d\n", total_ordinary_numbers);
}

int main(void)
{
	int test_cases;
	scanf("%d", &test_cases);

	for (int i = 0; i < test_cases; ++i)
		ordinary_numbers();

	return 0;
}

