#include<stdio.h>

#define MOD 1000000007
typedef unsigned long long llu;
llu arr[100009];

llu expmod(llu a, llu x, llu n){
  if(n==0)return a % MOD;
  else if(n%2==0)return expmod(a, x*x%MOD, n/2);
  else return expmod(a*x%MOD, x, n-1);
}

//  ([x,arr,n] + k*x^a) / (x^sum)
llu g(llu arr[], llu n, llu a, llu k, llu sum, llu x){
  if(a>=sum && k>0) return sum;
  else if(n>0 && arr[n-1]==a)return g(arr, n-1, a, k+1, sum, x);
  else if(k%x==0)return g(arr, n, a+1, k/x, sum, x);
  else return a;
}


int main(){
  unsigned nn, xx;
  llu i, n, x;
  llu sum=0;
  scanf("%u%u", &nn, &xx);
  n = (llu)nn;
  x = (llu)xx;

  for(i=0;i<n;i++){
    scanf("%llu", arr+i);
    sum+=arr[i];
  }

  for(i=0;i<n;i++)
    arr[i]=sum-arr[i];



  llu exp = (llu)g(arr, n, arr[n-1], 0, sum, x);
  llu result = expmod(1, x, exp);
  printf("%u",(unsigned)result);

  return 0;
}

