	ll pre = (y - 1)*n/2, add = n*n - n*n/2;
	if (n % 2 && y % 2 == 0)
		pre++;

	if (y % 2)
		pre += x / 2;
	else
		pre += (x + 1) / 2;

	printf("%I64d\n", pre + add);
}

