#include "vector"
int main(){
    //~ freopen("file.in", "r", stdin);
    int n;
    scanf("%d",&n);
    std::stack<int>s;
    for(int i=0;i<n;i++){
		int x;
		scanf("%d",&x);
		while(!s.empty() and s.top()==x){
			s.pop();
			x++;
		}
		s.push(x);
	}
	std::vector<int>ans;
	while(!s.empty()){
		ans.push_back(s.top());
		s.pop();
	}
	printf("%d\n",(int)ans.size());
	for(int i=int(ans.size()-1);i>=0;i--)
		printf("%d ",ans[i]);
	puts("");	
	return 0;
}

