	while (T--)
	{
		scanf("%d", &n);
		for (i = 1; i <= n; i++)
		{
			nC[i] = 0;
		}
		for (i = 0; i < n - 1; i++)
		{
			scanf("%d %d", &u, &v);
			N[u][nC[u]] = v;
			nC[u]++;
			N[v][nC[v]] = u;
			nC[v]++;
		}
