                        cnt[j]++;
                    else
                    {
                        strcpy(team[counter+1],t);
                        counter++;
                    }
                }
            }
            i=FindMax(cnt,counter);
            puts(team[i]);
        }
    }
    return 0;
}
int FindMax(int cnt[],int n)
{
    int j,i=0;
    for(j=i; j<n; j++)
    {
        if(cnt[i]<cnt[j])
            i=j;
    }
    return i;
}

