int b[100];

void bubble(int *a, int n)
{
	int i, j,tmp;
	for (i = 0; i < n; i++)
		for (j = 1; j < n - i; j++)
			if (a[j] > a[j - 1])
			{

				tmp = a[j];
				a[j] = a[j - 1];
				a[j - 1] = tmp;

				tmp = b[j];
				b[j] = b[j - 1];
				b[j - 1] = tmp;
			}
}

