	int n;
	int a[100];
	int i, j, k;
	scanf("%d", &n);
	for (i = 0; i < n; i++)
	{
		scanf("%d", &a[i]);
		b[i] = i+1;
	}
	bubble(a, n);
	for (i = 0; i < n - 2; i++)
		for (j = i + 1; j < n - 1; j++)
			for (k = j; k < n; k++)
				if (a[i] == a[j] + a[k])
				{
					printf("%d %d %d ", b[i], b[j],b[k]);
					return 0;
				}
	printf("-1\n");
	return 0;
}

