#include<stdio.h>
#include<stdlib.h>

int cmp(int a, int b)	{
	return a-b;
}

int main(int argc, char const *argv[])
{
	int q;
	scanf("%d", &q);
	while(q--)	{
		int n, r;
		scanf("%d %d", &n, &r);
		int monsters[n];
		int mx = -1;
		for (int i = 0; i < n; ++i)
		{
			scanf("%d", &monsters[i]);
		}

		int ans = 0;
		int moved = 0;
		int prev = -1;

		qsort(monsters, n, sizeof(int), cmp);
		for (int i = n-1; i >= 0; --i)
		{
			if(monsters[i] == prev)
				continue;
			if(monsters[i] <= moved)
				break;
			prev = monsters[i];
			++ans;
			moved += r;
		}

		printf("%d\n", ans);
	}
	return 0;
}

