	scanf("%d", &n);

	int steward[n];
	for (int i = 0; i < n; ++i)
	{
		scanf("%d", &steward[i]);
	}
	qsort(steward, n, sizeof(int), compare_ints);

	int count = 0;
	int flag;
	while (1)
