		flag = 1;
		if (steward[flag] == steward[0])
		{
			flag++;
			count++;
		}
		else
		{
			break;
		}
	}
	while (1)
	{
		flag = n - 2;
		if (steward[flag] == steward[n - 1])
		{
			flag--;
			count++;
		}
		else
		{
			break;
		}
	}
	printf("%d", n - count - 2);

	return 0;
}

