				int x = dp[a][b];

				if (x == -1)
					continue;
				dq[a & 1][b & 1] = max(dq[a & 1][b & 1], x);
				if (cc[0][i] == '1' && (b != 3 || a == 1))
					dq[b & 1][2] = max(dq[b & 1][2], x + 1);
				if (cc[1][i] == '1' && (b != 2 || a == 0))
					dq[b & 1][3] = max(dq[b & 1][3], x + 1);
				if (cc[0][i] == '1' && cc[1][i] == '1' && (a & 1) == (b & 1))
					dq[b | 2][b ^ 1 | 2] = max(dq[b | 2][b ^ 1 | 2], x + 2);
			}
		memcpy(dp, dq, sizeof dq);
	}
	ans = 0;
	for (a = 0; a < 4; a++)
		for (b = 0; b < 4; b++)
			ans = max(ans, dq[a][b]);
	printf("%d\n", ans);
	return 0;
}

