	scanf("%u", &num);
	int *directions = malloc(sizeof(unsigned int)*num);
	unsigned int i, j, found, min, currcord, glower, low;
	char c;
	getchar();
	for(i = 0; i < num; i++){
		scanf("%c", &c);
		if(c == 'L')directions[i] = -1;
		if(c == 'R')directions[i] = 1;
	}
	found = 0;	
	glower = 1;
	for(i = 0; i < num; i++){
		getchar();
		scanf("%u", &currcord);
		if(glower && directions[i] == 1){
			low = currcord;
			glower = 0;
		}
		if(!glower && directions[i] == -1){
			if(!found){
				min = (currcord - low)/2;
				found = 1;
			}			
			min = min > (currcord - low)/2 ? (currcord - low)/2 : min;
			glower = 1;
		}
	}

	if(!found) printf("-1");
	else printf("%u", min);
	return 0;
}

