#include <stdio.h>
#include <stdlib.h>

#define STACK_INIT_SIZE 100

typedef struct{

	int *top1;
	int *base1;
	int *top2;
	int *base2;

	int stacksize;
}SqStack;

void Init( SqStack *S )
{
	(*S).base1=(int *)malloc(STACK_INIT_SIZE*sizeof(int));

	if(!(*S).base1)
	 exit(0);//�ڴ����ʧ�� 

	(*S).top1=(*S).base1;
	(*S).base2=(*S).base1+STACK_INIT_SIZE;
	(*S).top2=(*S).base2;

}

int IsFull( SqStack *S )//�ж��Ƿ�ջ�� 
{
	return (*S).top1==(*S).top2;
}

int IsEmpty( SqStack *S )
{
	return (*S).top1==(*S).base1&&(*S).top2==(*S).base2;
}

void Push( SqStack *S,int e,int k )//����k���ж϶�ջ1�������Ƕ�ջ2����,Pop�е�k���ǡ� 
{
	if(IsFull)
	 exit(0);

	if(k==1)
	{
		*(*S).top1=e;
		 (*S).top1++;
	}
	if(k==2)
	{
		(*S).top2--;
	   *(*S).top2=e;
	}
}

int Pop( SqStack *S,int k)
{
	if(IsEmpty)
	 exit(0);

	int e;
	if(k==1)
	{
		if((*S).top1==(*S).base1)
		 exit(0);

		(*S).top1--;
		e=*(*S).top1;
	}

	if(k==2)
	{
		if((*S).top2==(*S).base2)
		 exit(0);

		e=*(*S).top2;
		(*S).top2++;
	}

	return e;
}

