int partition(int a[], int i, int j, int p) {
   int l = i;
   int r = j;

   while(l<=r) {
      while(a[l] < p) {
         l++;
      }

      while(r > 0 && a[r] >=p) {
         r--;
      }

      if(l >= r) {
         break;
      } else {
         swap(a,l,r);
      }
   }
   swap(a,l,j);
   return l;
}

void quickSort(int a[],int i, int j) {
   if(j>i){
      int p = a[j];
      int k = partition(a,i,j,p);
      quickSort(a,i,k-1);
      quickSort(a,k+1,j);
   }        
}

