#include <string.h>

#define MOD 1000000007

char str[1000001];
int f[1001][1001];

void fill_f(int a)
{
	if (f[a - 1][1])
		f[a][1] = (1 + 2 * f[a - 1][1]) % MOD;
	else
		fill_f(a - 1);
}

int compute_f(int a, int b)
{
	if (b == 1)
	{
		if (a == 1)
			return 1;
		else if (a >= 2)
		{
			if (a <= 100000)
			{
				if (!f[a - 1][1])
					fill_f(f);
				else
				{
					f[a][1] = 1 + 2 * f[a - 1][1];
					return f[a][1];
				}
			}
			else
				return compute_f(a - 1, b);
		}
	}
	else if (b >= 2)
	{
		return (compute_f(a, 1) + compute_f(a, b - 1)) % MOD;
	}
}

int main(void)
{
	int a, cnta = 0, cntb = 0, ans = 0;
	scanf("%s", str);
	f[1][1] = 1;
	for (a = 0; a <= strlen(str) - 1; a = a + 1)
