		if (str[a] == 'a')
			cnta++;
		else if (str[a] == 'b')
			cntb++;
		if ((a == strlen(str) - 1 && str[a] == 'b') || (str[a] == 'b' && str[a + 1] == 'a'))
		{
			ans = ans + compute_f(cnta, cntb);
			cntb = 0;
