int comparator(const void *a, const void *b)
{
    return (*(long long int *)b - *(long long int *)a);
}
int main()
{
    int t;
    scanf("%d", &t);
    while(t--)
    {
        int n, i;
        scanf("%d", &n);
        long long int a, b, k = 0, s[100];
