            scanf("%lld%lld", &a, &b);
            if(a >= b)
            {
                s[i] = a;
                k = k+b;
            }
            else
            {
                s[i] = b;
                k = k+a;
            }
        }
        //printf("%d\n", k);
        qsort(s, n, sizeof(long long int), comparator);
        printf("%lld\n", (2*k)+(s[0]*2));
    }
