/* upsolve with Dukkha */
#include <stdio.h>
#include <string.h>

#define N	300000

int main() {
	static char cc[N + 1];
	static char dp[N][9];
	int n, i, k, good;
	long long cnt;

	scanf("%s", cc);
	n = strlen(cc);
	cnt = 0;
	for (i = 0; i < n; i++)
		for (k = 3; k < 9 && i + k <= n; k++) {
			if (k % 2 == 1 && cc[i] == cc[i + k / 2] && cc[i] == cc[i + k - 1])
				good = 1;
			else
				good = dp[i][k - 1] || dp[i + 1][k - 1];
			if ((dp[i][k] = good))
				cnt++;
		}
	if (n >= 9)
		cnt += (long long) (n - 8) * (n - 7) / 2;
	printf("%lld\n", cnt);
	return 0;
}

