#include <stdio.h>
typedef long long unsigned int u64;
#define getlevel __builtin_ctzll
u64 n, q;
u64 go(char move, u64 v) {
    int level = getlevel(v);
    u64 testl, testr;
    switch(move) {
        case 'U':
            if(v == n >> 1) return v;
            if(v == 1) return 2;
            if(v == n - 1) return n - 2;
            testl = v + (1 << level);
            testr = v - (1 << level);
            if(getlevel(testl) == level + 1) return testl;
            else return testr;
        case 'L':
            if(v & 1) return v;
            return v - (1 << (level - 1));
        case 'R':
            if(v & 1) return v;
            return v + (1 << (level - 1));
    }
}
int main(void) {
    scanf("%llu%llu\n", &n, &q);
    n++;
    for(int i = 0; i < q; i++) {
        u64 v;
        scanf("%llu\n", &v);
        for(int c = getchar(); c != EOF && c != '\n'; c = getchar()) {
            v = go(c, v);
        }
        printf("%llu\n", v);
    }
    return 0;
}

