#include <stdlib.h>
int Find1(int, int);
int Find2(int, int);
int main()
{
  int a[1000], b[1000], i = 0, m = 1, n = 1, temp = 0;
  scanf("%d %d", &m, &n);
  for(i = 1; i <= m+1; i++) scanf("%d", &a[i]);
  for(i = 1; i <= n+1; i++) scanf("%d", &b[i]);
  if(m > n){
          if(a[1]*b[1] < 0) printf("-Infinity\n");
          else printf("Infinity\n");
  }
  else if(m < n) printf("0/1\n");
  else{
       temp = a[1];
       a[1] = Find1(a[1],b[1]);
       b[1] = Find2(temp,b[1]);
       printf("%d/%d\n", a[1], b[1]);
  }
  return 0;
}

int Find1(int a, int b){
    int flag = 0; int m = a;
    if(a*b < 0) flag = 1;
    a = abs(a); b = abs(b);
    while(a!=b){
         if(a > b) a = a - b;
         else b = b - a;
    }
    if(flag == 1) return -(m/a);
    else return m/a;
}

int Find2(int a, int b){
    int m = b;
    a = abs(a); b = abs(b);
    while(a != b){
         if(a > b) a = a - b;
         else b = b - a;
    }
    return abs(m/a);
}

