#include<stdio.h>
void conquer(int arr[], int mid, int left, int right){
	int temp[right-left+1];
	int kiri=left;
	int kanan=mid+1;
	int idx=0, i;
	while(kiri<=mid && kanan<=right){
		if(arr[kiri]>arr[kanan]) temp[idx++]=arr[kiri++];
		else temp[idx++]=arr[kanan++];
	}
	while(kiri<=mid){
		temp[idx++]=arr[kiri++];
	}
	while(kanan<=right){
		temp[idx++]=arr[kanan++];
	}
	for(i=0; i<idx; i++){
		arr[left+i]=temp[i];
	}
}
void merge(int arr[],int left, int right){
	if(left<right){
		int mid=(right+left)/2;
		merge(arr,left,mid);
		merge(arr,mid+1,right);
		conquer(arr,mid,left,right);
	}
}
